INSERT INTO `courses` (`_id`, `ugrad`, `grad`, `prefix`, `number`, `slug`, `title`, `description`, `min_hours`, `max_hours`, `variable_hours`, `requisites`, `semesters`, `uge`, `kstate8_text`, `created_at`, `updated_at`) VALUES
(1, 1, 0, 'ACCTG', 231, 'ACCTG231', 'Accounting for Business Operations', 'An introduction to the operating activities of businesses and the roles that accounting information plays in planning, evaluating, and recording those activities. An introduction to financial statements is included.', 3, 3, 0, 'Prerequisite: MATH 100 and GENBA 110 or sophomore standing.', 'Fall, Spring, Summer', 1, 'Empirical and Quantitative Reasoning', NULL, NULL),
(2, 1, 0, 'ACCTG', 241, 'ACCTG241', 'Accounting for Investing and Financing', 'Extends the concepts of planning and evaluation to the business activities of acquiring, disposing, and financing productive assets. Financial statement analysis will be covered.', 3, 3, 0, 'Prerequisite: ACCTG 231.', 'Fall, Spring, Summer', 1, 'Empirical and Quantitative Reasoning', NULL, NULL),
(961, 1, 0, 'CIS', 15, 'CIS015', 'Undergraduate Seminar', 'Presentation of professional problems and practices by students, faculty, and industry professionals associated with computing and information sciences.', 0, 0, 0, '', 'Fall, Spring', 0, 'None', NULL, NULL),
(962, 1, 0, 'CIS', 101, 'CIS101', 'Introduction to Computing Systems, Information Search, and Security', 'Introduction to basic computer and Internet security including basic computer security and privacy issues, computer networks, malware protection; effective use of the Internet as a resource including website evaluation and effective search tools and techniques.', 1, 1, 0, '', 'Fall, Spring, Summer', 0, 'Ethical Reasoning and Responsibility', NULL, NULL),
(963, 1, 0, 'CIS', 102, 'CIS102', 'Introduction to Spreadsheet Applications', 'Designing, building, and modifying spreadsheets. Addressing techniques and formatting. Use of formulas and functions. Spreadsheets as management and decision tools. Charting of data.', 1, 1, 0, '', 'Fall, Spring, Summer', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(964, 1, 0, 'CIS', 103, 'CIS103', 'Introduction to Database Applications', 'Design, create, modify, and maintain relational databases. Create relationships. Add and modify data. Search and query database. Design and create screen forms and reports.', 1, 1, 0, '', 'Fall, Spring, Summer', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(965, 1, 0, 'CIS', 104, 'CIS104', 'Introduction to Word Processing Applications', 'Basic features of word processing. Create and edit a document. View, format, and customize document. Revise, update, and rearrange text. Add graphics. Support features. Merge documents.', 1, 1, 0, '', 'Fall, Spring, Summer', 0, 'None', NULL, NULL),
(966, 1, 0, 'CIS', 105, 'CIS105', 'Introduction to Computer Programming', 'An introduction to computer problem solving; basic programming logic; algorithms; control structures; and data structures. The focus is on the control flow of programming with an elementary introduction to object-oriented design. Upon successful completion of the course, students should be able to write simple computer programs.', 1, 1, 0, '', 'Fall, Spring, Summer', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(967, 1, 0, 'CIS', 111, 'CIS111', 'Introduction to Computer Programming', 'Introduction to programming. Principles and applications of programming fundamentals: state, control structures, methods, and arrays. Programming projects.', 3, 3, 0, 'Prerequisite: MATH 100. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall, Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(968, 1, 0, 'CIS', 115, 'CIS115', 'Introduction to Computing Science', 'A survey of the discipline of Computing Science and its interaction with other disciplines, incorporating historical development, theories, and tools of Computing Science (algorithm design and programming). Topics include: mechanical computers, digital computers, bioinformatics, microcontrollers, robotics, security, scientific computing, simulation, and web technologies.', 3, 3, 0, 'Prerequisite: None.', 'Fall, Spring', 0, 'None', NULL, NULL),
(969, 1, 0, 'CIS', 190, 'CIS190', 'Undergraduate Seminar in Computing and Information Sciences', 'Topics of special interest in computing and information sciences.', 1, 3, 0, '', 'Fall', 0, 'None', NULL, NULL),
(970, 1, 0, 'CIS', 200, 'CIS200', 'Programming Fundamentals', 'Principles of algorithm design and their application to procedural programming: state, control structures, methods. Patterns of conditional and iterative control structure.&nbsp; Program testing.&nbsp;Introduction to arrays, classes, and objects.&nbsp; Programming projects.', 4, 4, 0, 'Prerequisite: MATH 100 and either CIS 115 OR ECE 241. \n\nStudents may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall, Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(971, 1, 0, 'CIS', 209, 'CIS209', 'C Programming for Engineers', 'Application of computers to engineering problems. Abstraction and problem solving; algorithms; control structures; input/output; functions; arrays and array processing.', 3, 3, 0, 'Prerequisite: MATH 220. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall, Spring, Summer', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(972, 1, 0, 'CIS', 300, 'CIS300', 'Data and Program Structures', 'A study of common data and program structures together with associated algorithms. Topics include interfaces, design patterns, arrays, stacks, queues, lists, trees, hash tables, recursion, binary search, and tree traversals. Experience with both use and implementation of these structures and algorithms using a modern programming language. Discussion of tradeoffs involving performance and software maintainability.', 3, 3, 0, 'Prerequisite: CIS 200. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall, Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(973, 1, 0, 'CIS', 301, 'CIS301', 'Logical Foundations of Programming', 'Logical formalisms used to model and reason about computer systems. Propositional and predicate logic, syntax, semantics, and proof theory; soundness and completeness issues. Mathematical induction. Program verification: invariants and program logics.', 3, 3, 0, 'Prerequisite: CIS 200. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall, Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(974, 1, 0, 'CIS', 308, 'CIS308', 'C/C++ Language Laboratory', 'Fundamentals of programming in C and C++; applications.', 1, 1, 0, 'Prerequisite: CIS 300. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall, Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(975, 1, 0, 'CIS', 362, 'CIS362', 'Introduction to Business Programming', 'An introduction to basic business programming techniques including file manipulation operations and sorting. The COBOL language will be used as an implementation tool.', 3, 3, 0, 'Prerequisite: CIS 200. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(976, 1, 0, 'CIS', 397, 'CIS397', 'Honors Seminar in Computer Science', 'Honors Seminar in Computer Science', 1, 3, 0, '', '', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(977, 1, 0, 'CIS', 415, 'CIS415', 'Computers and Society', 'A study of the impact of computers and associated technologies on society, including such topics as ethics of computer use, computer fraud, protection of privacy; legal, moral, and public policy-making responsibility of computer professionals.', 1, 1, 0, 'Prerequisite: CIS 300. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall', 0, 'Ethical Reasoning and Responsibility', NULL, NULL),
(978, 1, 0, 'CIS', 450, 'CIS450', 'Computer Architecture and Operations', 'Introduction to modern computer architectures: register transfer abstraction, addressing modes, basic operations (data transfer, arithmetic/logic, and control operations). Basic Operating System concepts and operations. Understanding relationships of higher-level language constructs to corresponding assembly instruction sequences generated by compilers. Introduction to interrupts and low-level I/O operations.', 3, 3, 0, 'Prerequisite: ECE 241. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses. Prerequisite or concurrent enrollment: CIS 308.', 'Fall, Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(979, 1, 0, 'CIS', 490, 'CIS490', 'Special Topics in Computer Science', 'Current topics in computer science.', 2, 4, 0, 'Prerequisite: Varies with the announced topic.', '', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(980, 1, 0, 'CIS', 499, 'CIS499', 'Honors Research/Thesis', 'Individual research problem selected with approval of faculty advisor. A report/thesis is presented orally and in writing during the last semester.', 2, 4, 0, '', '', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(981, 1, 1, 'CIS', 501, 'CIS501', 'Software Architecture and Design', 'Principles and patterns for design and structure of software, development of object-oriented models, examples of software architecture.', 3, 3, 0, 'Prerequisite: CIS 300. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall, Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(982, 1, 1, 'CIS', 505, 'CIS505', 'Introduction to Programming Languages', 'History, programming language concepts including type, scope and extent, abstraction mechanisms and control; programming paradigms, including one or more of logic, functional, and object-oriented programming, and programming languages supporting the selected paradigms; interpreters, compilers and virtual machines for the selected languages; storage management and garbage collection; type checking and type inference.', 3, 3, 0, 'Prerequisite: CIS 300, CIS 301. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall, Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(983, 1, 1, 'CIS', 520, 'CIS520', 'Operating Systems I', 'Operating systems concepts, services, and implementation: interrupt processing, processes, concurrency, deadlocks, resource scheduling and system structure; resource management: real and virtual storage, input/output systems, disk scheduling and file systems; design and construction of concurrent programs.', 3, 3, 0, 'Prerequisite: CIS 450 or ECE 431; CIS 308, and CIS 501. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall, Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(984, 1, 1, 'CIS', 521, 'CIS521', 'Real-Time Programming Laboratory', 'Project-oriented introduction to asynchronous processes and related systems software: device drivers, event-driven operations, hierarchical and time-sliced process scheduling, spooling operations, interjob and intermachine communications. Projects will be built on a single-use environment.', 3, 3, 0, 'Prerequisite: ECE 241 and CIS 350, concurrent enrollment CIS 520. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall', 0, 'None', NULL, NULL),
(985, 1, 1, 'CIS', 522, 'CIS522', 'Introduction to Data Structures', 'Introduction to basic data structures such as stacks, queues, lists, and priority queues, and algorithmic techniques for sorting, searching and hashing. Emphasis on modularity and reuse. Introduction to the concept of object-oriented design and interface specifications.', 1, 1, 0, 'Prerequisite: C, or C++ programming.', 'Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(986, 1, 1, 'CIS', 523, 'CIS523', 'Introduction to Concurrent Programming', 'Introduction to concurrent programming techniques based on message passing primitives (send/receive) and shared memory (semaphore based P/V operations and monitor-based wait/signal operations).', 1, 1, 0, 'Prerequisite: C, or C++ programming and CIS 300 or CIS 522. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(987, 1, 1, 'CIS', 525, 'CIS525', 'Telecommunications and Data Communication Systems', 'Basic concepts including OSI 7 layer model, data transmission methods, medium access, link control, connections management; network applications including electronic mail, file transfer, distributed computing, window systems; network management including OSI and Internet management frameworks.', 3, 3, 0, 'Prerequisite: CIS 300. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', '', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(988, 1, 1, 'CIS', 526, 'CIS526', 'Web Interface Design', 'Fundamental principles and best practices of interface design, web service design, advanced web interfaces, web development frameworks, web standards and accessibility issues.', 3, 3, 0, 'Prerequisite: CIS 501, CMST 135. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring', 0, 'None', NULL, NULL),
(989, 1, 1, 'CIS', 530, 'CIS530', 'Introduction to Artificial Intelligence', 'Fundamental background in intelligent systems for undergraduate students. Intelligent agents, problem-solving, uninformed and informed (heuristic) search, game tree search, foundations of knowledge representation and reasoning using logic and probability, and a first introduction to planning and machine learning. AI topics include the problem areas of natural language processing (NLP) and perception and methodologies such as artificial neural networks and genetic and evolutionary computation. Applications to practical design and development of intelligent systems will be emphasized, leading to individual projects on current topics and applications in AI.', 3, 3, 0, 'Prerequisite: CIS 300, CIS 301 and MATH 312 or 510. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(990, 1, 1, 'CIS', 536, 'CIS536', 'Introduction to Computer Graphics', 'Introduction to computer graphics for undergraduate students. Mathematical foundations (e.g., linear, affine, and projective transformations) and fundamental topics in realistic rendering: view normalization, clipping and culling, scan conversion of lines and polygons, shading and illumination, texture mapping, user interfaces, picking, and collision handling. Intermediate topics include shaders, procedural textures, particle systems, basics of animation, fractals, color theory, and ray tracing.', 3, 3, 0, 'Prerequisite: CIS 308 and either MATH 205 or MATH 220. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring', 0, 'None', NULL, NULL),
(991, 1, 1, 'CIS', 540, 'CIS540', 'Software Engineering Project I', 'The first semester of a two-semester capstone course. Current practices of software development, requirements, design, prototyping, measures and evaluation. Specification, design, and prototyping of a software system.', 3, 3, 0, 'Prerequisite: CIS 308, 501, and STAT 325 or 510. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(992, 1, 1, 'CIS', 541, 'CIS541', 'Software Engineering Project II', 'Continuation of CIS 540. Final implementation, integration, and testing of a software system. Introduction to configuration management, project management, and software maintenance.', 3, 3, 0, 'Prerequisite: CIS 540 (which must be taken in the preceding semester). Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(993, 1, 1, 'CIS', 543, 'CIS543', 'Software Engineering Design Project', 'Current practices of software development, requirements, design, prototyping, measures and evaluations. Specification, design, and prototyping of a software system.', 3, 3, 0, 'Prerequisite: CIS 308, 501, and STAT 325 or 510. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(994, 1, 1, 'CIS', 544, 'CIS544', 'Advanced Software Design and Development', 'Advanced concepts and practicum in object-oriented analysis, modeling, design, implementation, testing, and use of CASE tools; relationships among structural, static, and dynamic models; relationship among conceptual, system, and implementation models.', 3, 3, 0, 'Prerequisite: CIS 501. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring, Summer', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(995, 1, 1, 'CIS', 548, 'CIS548', 'Software Management', 'Topics related to the management of software, including organization, project planning, process models, life cycle models, TQM, software quality assurance, cost estimation, configuration management.', 3, 3, 0, 'Prerequisite: CIS 562. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring, Summer', 0, 'None', NULL, NULL),
(996, 1, 1, 'CIS', 551, 'CIS551', 'Introduction to Computer and Information Security', 'An introduction to computer and information security, including common attack techniques, application of cryptography in security, authentication and authorization, network security, enterprise network defense, web security, and analysis of design flaws that render a system vulnerable. Course projects provide hands-on experience on both the defense and offense aspects in cyberspace.', 3, 3, 0, 'Prerequisite: CIS 450 or 520. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(997, 1, 1, 'CIS', 553, 'CIS553', 'Intro Applied Cryptography', 'Introduction to the fundamentals of cryptography, including mathematical background, classical ciphers, public key encryption, block and stream ciphers, cryptographic hash functions, digital signatures, common attacks, and basic security protocols.', 3, 3, 0, 'Prerequisite: CIS 300, MATH 510. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(998, 1, 1, 'CIS', 560, 'CIS560', 'Database System Concepts', 'Concepts, approaches, and techniques in database management. Representation of information as data, data storage techniques, foundations of logical data models, data retrieval, database design, transaction management, integrity and security.', 3, 3, 0, 'Prerequisite: CIS 300, CIS 301, and MATH 312 or 510. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(999, 1, 1, 'CIS', 562, 'CIS562', 'Enterprise Information Systems', 'Conceptual models for E-Commerce and for business applications, database management systems, relational data model, normal forms, query language, security features, web access and scripting languages, development process and management, issues in management of enterprise information systems.', 3, 3, 0, 'Prerequisite: CIS 501. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(1000, 1, 1, 'CIS', 570, 'CIS570', 'Introduction to Formal Language Theory', 'Formal languages, automata, regular expressions, grammars, introduction to computability theory. Reading and writing informal mathematical proofs pertaining to these topics.', 3, 3, 0, 'Prerequisite: MATH 510. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(1001, 1, 1, 'CIS', 575, 'CIS575', 'Introduction to Algorithm Analysis', 'An introduction to mathematical analysis of time- and space-complexity of algorithms, including worst-case, average-case, and amortized complexity. An examination of various algorithmic designs, such as greedy algorithms, divide-and-conquer algorithms, and dynamic programming algorithms. Techniques for proving correctness of algorithms.', 3, 3, 0, 'Prerequisite: CIS 300, CIS 301, and MATH 510.', 'Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(1002, 1, 1, 'CIS', 580, 'CIS580', 'Fundamentals of Game Programming', 'Fundamental principles of programming games. Foundational game algorithms and data structures. Two-dimensional graphics and physics simulation. Development for multiple platforms. Utilization of game programming libraries. Design of multiple games incorporating topics covered.', 3, 3, 0, 'Prerequisite: CIS 501, MATH 221 and prerequisite or concurrent enrollment: PHYS 213 or PHYS 223.', 'Fall', 0, 'Aesthetic Interpretation\nEmpirical and Quantitative Reasoning', NULL, NULL),
(1003, 1, 1, 'CIS', 585, 'CIS585', 'Game Engine Design', 'Current practices of game engine development. The game engine as a soft real-time multi-agent simulation.&nbsp; Three-dimensional graphics and animation techniques, scene management, physics simulation, event systems, resource management, and network game architectures.&nbsp; Design and prototyping of a general use game engine.', 3, 3, 0, 'Prerequisite: CIS 580 and MATH 551.', 'Spring', 0, 'None', NULL, NULL),
(1004, 1, 1, 'CIS', 590, 'CIS590', 'Special Topics in Computer Science', 'Selected topics in computer science.', 1, 4, 0, 'Prerequisite: Permission of instructor.', 'On sufficient demand', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(1005, 1, 1, 'CIS', 597, 'CIS597', 'Information Systems Project', 'Directed studies: selection and completion of a project; may include collaboration with other students; may include design, analysis, and implementation. Completion of a plan, two progress reviews, and a final report with literature review and project evaluation.', 3, 3, 0, 'Prerequisite: CIS 562 and senior standing in information systems. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall, Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(1006, 1, 1, 'CIS', 598, 'CIS598', 'Computer Science Project', 'Directed studies: selection, investigation and report on some topic not covered in prior courses; may include an implementation and/or experimentation component; may be done in collaboration with other students. Completion of a plan, two reviews, and a final report with literature review and project evaluation.', 3, 3, 0, 'Prerequisite: CIS 560 and senior standing in computer science. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall, Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(1007, 1, 1, 'CIS', 604, 'CIS604', 'Set Theory and Logic for CS', 'Informal and axiomatic set theory, propositional and predicate logic, proof techniques.', 3, 3, 0, 'Prerequisite: Graduate standing.', '', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(1008, 1, 1, 'CIS', 621, 'CIS621', 'Real-Time Programming Fundamentals', 'Relationship between C/C++ constructs and corresponding assembly code generated by compilers. Introduction to special techniques used to implement micro-controllers, such as initialization of programmable CPU modules/peripheral devices, techniques to link assembly and C/C++ codes, producing ROM-able code, and EPROM burning/Flash programming.', 1, 1, 0, 'Prerequisite: C or C ++ programming, and CIS 523 and either CIS 300 or CIS 522. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(1009, 1, 1, 'CIS', 622, 'CIS622', 'Real-Time Operating Systems', 'Basic real-time operating systems concepts and services; interrupt processing; process and thread models; real-time software architectures and development environments. Detailed study of design and implementation of real-time applications using real-time operating systems.', 1, 1, 0, 'Prerequisite: CIS 621. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(1010, 1, 1, 'CIS', 625, 'CIS625', 'Concurrent Software Systems', 'Architecture, design, modeling, implementation, and verification of concurrent, parallel, and distributed software; aspects such as real-time programming, parallel simulation; fault-tolerant programming; grid computing, embedded systems control.', 3, 3, 0, 'Prerequisite: CIS 501. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(1011, 1, 1, 'CIS', 635, 'CIS635', 'Introduction to Computer-Based Knowledge Systems', 'Introduction to the applications of artificial intelligence concepts to solving knowledge-dependent tasks. Review of knowledge-representation ideas. Survey of expert system design. Introduction to existing knowledge-based tools available on personal computers. Development of an intelligent system.', 3, 3, 0, 'Prerequisite: CIS 300. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(1012, 1, 1, 'CIS', 636, 'CIS636', 'Introduction to Computer Graphics', 'Devices and software for graphics display and user interaction, development of software for direct graphic manipulation applications.', 3, 3, 0, 'Prerequisite: CIS 300. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall, Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(1013, 1, 1, 'CIS', 638, 'CIS638', 'Multimedia Systems', 'Introduction to computer graphics devices, user interaction; history and scope; multimedia structure, encoding methods and standards, mark-up and scripting languages, software tools, and applications; readings in current literature; class presentation; multimedia project.', 3, 3, 0, 'Prerequisite: CIS 300, senior standing. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring', 0, 'None', NULL, NULL),
(1014, 1, 1, 'CIS', 640, 'CIS640', 'Software Testing Techniques', 'Survey of software testing methodologies; evaluation of software testing strategies; experience in a variety of software testing practices.', 3, 3, 0, 'Prerequisite: CIS 501. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(1015, 1, 1, 'CIS', 645, 'CIS645', 'Software Development Environments', 'Survey of current development environments and/or an in-depth study of one development environment.', 3, 3, 0, 'Prerequisite: CIS 501. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'On sufficient demand', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(1016, 1, 1, 'CIS', 690, 'CIS690', 'Implementation Project', 'The department will suggest various design or implementation projects for individuals or groups in areas such as translators, interpreters, microprogramming, minicomputer operating systems, graphics, numerical software, etc.', 3, 3, 0, 'Prerequisite: Junior standing.', 'Fall, Spring, Summer', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(1017, 1, 1, 'CIS', 697, 'CIS697', 'Seminar in Computer Science', 'Seminar in Computer Science', 1, 3, 0, 'Prerequisite: Junior standing.', '', 0, 'None', NULL, NULL),
(1018, 1, 1, 'CIS', 705, 'CIS705', 'Programming Languages', 'Programming language concepts, including typing, scoping, abstraction and exceptions; programming paradigms, including one or more of logic, functional, object-oriented and concurrent programming, and programming languages supporting the selected paradigms; introduction to programming language semantics; programming language design; development environments.', 3, 3, 0, 'Prerequisite: CIS 505 and MATH 510. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring', 0, 'None', NULL, NULL),
(1019, 1, 1, 'CIS', 706, 'CIS706', 'Translator Design I', 'Compilers and interpreters, including description of languages, finite state scanners. LL(1) parsing, symbol tables, syntax-directed semantics, simple code generation. Constructing a simple compiler.', 3, 3, 0, 'Prerequisite: CIS 501, and CIS 605. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall', 0, 'None', NULL, NULL),
(1020, 1, 1, 'CIS', 710, 'CIS710', 'Computer Simulation Experiments', 'Principles of digital computer simulation; discrete simulation method, statistics of simulations; implementations.', 3, 3, 0, 'Prerequisite: CIS 300. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'On sufficient demand', 0, 'None', NULL, NULL),
(1021, 1, 1, 'CIS', 720, 'CIS720', 'Advanced Operating Systems', 'Process synchronization and communication, distributed programming primitives, transactions and concurrency control, distributed scheduling, distributed storage, deadlock, security.', 3, 3, 0, 'Prerequisite: CIS 520. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', '', 0, 'None', NULL, NULL),
(1022, 1, 1, 'CIS', 721, 'CIS721', 'Real-Time Systems', 'The design of hard real-time embedded systems, including language and operating system support, scheduling, schedulability analysis, fault tolerance, and design tools.', 3, 3, 0, 'Prerequisite: CIS 520 or both CIS 622 and ECE 633. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', '', 0, 'None', NULL, NULL),
(1023, 1, 1, 'CIS', 722, 'CIS722', 'Operating System Practices', 'Structure and functions of modern operating systems. Emphasis on reading and modifying the source code of a working operating system. This includes memory management, input/output, process management, file systems, and network interconnection software construction.', 3, 3, 0, 'Prerequisite: CIS 520. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring', 0, 'None', NULL, NULL),
(1024, 1, 1, 'CIS', 725, 'CIS725', 'Advanced Computer Networks', 'Network algorithms; routing and congestion control; protocol engineering; protocol decomposition, specification and verification, synthesis; protocols for high speed networks; parallel implementations, light-weight protocols.', 3, 3, 0, 'Prerequisite: CIS 520, and CIS 525. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', '', 0, 'None', NULL, NULL),
(1025, 1, 1, 'CIS', 726, 'CIS726', 'Advanced World Wide Web Technologies', 'An advanced course on the technologies that make up the World Wide Web. WWW site designs and analysis, WWW software architecture, server-side technologies, dynamic executable scheduling, digital libraries, WWW security.', 3, 3, 0, 'Prerequisite: CIS 520, CIS 525. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring', 0, 'None', NULL, NULL),
(1026, 1, 1, 'CIS', 730, 'CIS730', 'Principles of Artificial Intelligence', 'Introduction to the fundamental concepts and techniques of AI: problem solving, search and planning, knowledge representation and qualitative reasoning, expert systems, natural language processing and cognitive modeling, computer vision, and machine learning.', 3, 3, 0, 'Prerequisite: CIS 501. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring', 0, 'None', NULL, NULL),
(1027, 1, 1, 'CIS', 732, 'CIS732', 'Machine Learning and Pattern Recognition', 'Theory and methodology of inductive learning, including decision trees, artificial neural networks, probabilistic and instance-based learning, and inductive logic programming; unsupervised and reinforcement learning, bagging and boosting; genetic algorithms and genetic programming; and applications to data mining.', 3, 3, 0, 'Prerequisite: CIS 501 or CIS 575.', 'Fall', 0, 'None', NULL, NULL),
(1028, 1, 1, 'CIS', 734, 'CIS734', 'Introduction to Genomics and Bioinformatics', 'A problem solving approach to understanding genomics and bioinformatics. Practical use of databases and web-based tools used to study biological problems. Introduction to the algorithms behind these tools.', 4, 4, 0, 'Prerequisite: Either BIOL 450 or CIS 300, and instructor permission. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring, even years', 0, 'None', NULL, NULL),
(1029, 1, 1, 'CIS', 736, 'CIS736', 'Computer Graphics', 'Topics in computer representation and display of images and graphic interaction.', 3, 3, 0, 'Prerequisite: CIS 308. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', '', 0, 'None', NULL, NULL),
(1030, 1, 1, 'CIS', 740, 'CIS740', 'Software Engineering', 'Software life cycle, requirements, specifications, design, validation, measures, and maintenance.', 3, 3, 0, 'Prerequisite: CIS 540. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', '', 0, 'None', NULL, NULL),
(1031, 1, 1, 'CIS', 744, 'CIS744', 'Advanced Software Analysis and Design', 'Advanced concepts and practicum in object-oriented analysis, modeling, design, implementation, testing, and use of CASE tools; relationships among structural, static, and dynamic models; relationship among conceptual, system, and implementation models.', 3, 3, 0, 'Prerequisite: CIS 540. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring, Summer', 0, 'None', NULL, NULL),
(1032, 1, 1, 'CIS', 746, 'CIS746', 'Software Measurement', 'Measurement theory; development, validation and use of software measures; software measures in the life cycle, including cost estimation, design measures, software complexity and software reliability.', 3, 3, 0, 'Prerequisite: CIS 540. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', '', 0, 'None', NULL, NULL),
(1033, 1, 1, 'CIS', 748, 'CIS748', 'Advanced Software Management', 'Topics related to the management of software, including organization, project planning, process models, life cycle models, TQM, software quality assurance, cost estimation, configuration management.', 3, 3, 0, 'Prerequisite: CIS 740. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring, Summer', 0, 'None', NULL, NULL),
(1034, 1, 1, 'CIS', 750, 'CIS750', 'Advanced Computer Architecture Experiments', 'Characteristics of various computers including those with execution support of multiprocessing, multiprogramming, microprogrammable, high-level language, stack processing, and communication architectures.', 3, 3, 0, 'Prerequisite: CIS 450. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'On sufficient demand', 0, 'None', NULL, NULL),
(1035, 1, 1, 'CIS', 751, 'CIS751', 'Computer and Information Security', 'A comprehensive coverage of computer and information security. Basic cryptography, access control, authentication, authorization, network security, software security, and social aspects of security. The lectures discuss when and where things can go wrong and how design flaws in a system can be exploited to compromise security. Common attack techniques are introduced, and students have the opportunity to work on course projects that cover both the defense and offense aspects in cyberspace.', 3, 3, 0, 'Prerequisite: CIS 450 or 520. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall', 0, 'None', NULL, NULL),
(1036, 1, 1, 'CIS', 753, 'CIS753', 'Applied Cryptography', 'Fundamentals of cryptography, including mathematical background, classical ciphers, public key encryption, block and stream ciphers, cryptographic hash functions, digital signatures, common attacks, and basic network security.', 3, 3, 0, 'Prerequisite: CIS 300, MATH 510, or equivalent. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring', 0, 'None', NULL, NULL),
(1037, 1, 1, 'CIS', 755, 'CIS755', 'Advanced Computer and Information Security', 'In-depth coverage of advanced theoretical and practical security techniques with emphasis on construction of new systems and auditing and repair of existing security-critical systems using rigorous design, risk analysis, and engineering methods and application of state-of-art theoretical tools.', 3, 3, 0, 'Prerequisites: CIS 551 or CIS 751. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Spring', 0, 'None', NULL, NULL),
(1038, 1, 1, 'CIS', 761, 'CIS761', 'Data Base Management Systems', 'Data models and languages, hierarchical, network, relational systems; implementation and operational requirements; programming projects using data base management systems.', 3, 3, 0, 'Prerequisite: CIS 560, CIS 604. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', '', 0, 'None', NULL, NULL),
(1039, 1, 1, 'CIS', 762, 'CIS762', 'Office Automation', 'Characteristics of information work; modeling systems for characterizing aspects of office environment; form-based systems; office automation and description languages, ergonomics, local area networks and tools used in the automation of offices.', 3, 3, 0, 'Prerequisite: CIS 525, 560. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', '', 0, 'None', NULL, NULL),
(1040, 1, 1, 'CIS', 764, 'CIS764', 'Database Design', 'Conceptual, logical, physical, and user interface design for database management systems.', 3, 3, 0, 'Prerequisite: CIS 501. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'On sufficient demand', 0, 'None', NULL, NULL),
(1041, 1, 1, 'CIS', 770, 'CIS770', 'Formal Language Theory', 'Regular languages, finite automata, context-free languages, pushdown automata, context-sensitive languages, linear bounded automata, recursively enumerable languages, Turing machines.', 3, 3, 0, 'Prerequisite: MATH 510. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', '', 0, 'None', NULL, NULL),
(1042, 1, 1, 'CIS', 771, 'CIS771', 'Software Specification', 'Formal logic or specification of software components; algebraic vs. model-based specifications; common abstract types; verification of properties of specifications; introduction to specification of concurrent systems.', 3, 3, 0, 'Prerequisite: CIS 301. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', '', 0, 'None', NULL, NULL),
(1043, 1, 1, 'CIS', 775, 'CIS775', 'Analysis of Algorithms', 'Study and application of techniques and procedures used in the analysis of algorithms including the worst and average cases of both time and space. Study of the P and NP classes.', 3, 3, 0, 'Prerequisite: CIS 575. Students may enroll in CIS courses only if they have earned a grade of C or better for each prerequisite to those courses.', 'Fall', 0, 'None', NULL, NULL),
(1044, 1, 1, 'CIS', 798, 'CIS798', 'Topics in Computer Science', 'Topics in Computer Science', 0, 18, 1, 'Prerequisite: Prerequisite varies with the announced topic.', '', 0, 'None', NULL, NULL),
(1410, 1, 0, 'ECE', 15, 'ECE015', 'New Student Assembly', 'Assemblies are held once a month for new electrical engineering and computer engineering students to provide them information on the department. The students get an opportunity to meet student leaders, department faculty, and learn about department activities.', 0, 0, 0, '', 'Fall, Spring, Summer', 0, 'None', NULL, NULL),
(1411, 1, 0, 'ECE', 210, 'ECE210', 'Introduction to Electrical Engineering', 'Basic circuit theory including voltage, current, Kirchoff’s laws, and Ohm’s law. Introduction to circuit elements, application circuits, complex impedance and power, and additional theory fundamental and the EE curriculum. Includes laboratory work (e.g., with multimeters and oscilloscopes) and circuit construction techniques.', 3, 3, 0, 'Prerequisite or concurrent enrollment: MATH 220.', 'Fall, Spring', 0, 'Natural and Physical Sciences', NULL, NULL),
(1412, 1, 0, 'ECE', 241, 'ECE241', 'Introduction to Computer Engineering', 'Number systems, Boolean algebra fundamentals, gates, flip-flops, shift registers, memories, etc.; basic engineering aspects of computer architecture; introduction to hardware description languages.', 3, 3, 0, '', 'Fall, Spring', 0, 'Empirical and Quantitative Reasoning', NULL, NULL),
(1413, 1, 0, 'ECE', 410, 'ECE410', 'Circuit Theory I', 'An introduction to linear circuit theory; analysis of linear circuits containing resistance, inductance, and capacitance. Mutual inductance and transformers.', 3, 3, 0, 'Prerequisite: MATH 221 and ECE 210.', 'Fall, Spring', 0, 'None', NULL, NULL),
(1414, 1, 0, 'ECE', 431, 'ECE431', 'Microcontrollers', 'Architecture, assembly language programming, serial and parallel input/output, interface circuits, and applications.', 3, 3, 0, 'Prerequisite: ECE 241; and CIS 200 or 209.', 'Fall, Spring', 0, 'None', NULL, NULL),
(1415, 1, 0, 'ECE', 441, 'ECE441', 'Design of Digital Systems', 'Design of combinational and sequential systems including asynchronous circuits; testing of digital systems. Emphasis is placed on hardware description languages, computer-aided design tools, and simulations.', 3, 3, 0, 'Prerequisite: ECE 210 and ECE 241.', 'Fall, Spring', 0, 'None', NULL, NULL),
(1416, 1, 0, 'ECE', 499, 'ECE499', 'Honors Research in Electrical and Computer Engineering', 'Individual research problem selected with approval of faculty advisor. Open to students in the College of Engineering honors program. A report is presented orally and in writing during the last semester.', 0, 18, 1, '', 'Fall, Spring', 0, 'None', NULL, NULL),
(1417, 1, 1, 'ECE', 502, 'ECE502', 'Electronics Laboratory', 'Design, simulation, construction and testing of electronic circuits.', 2, 2, 0, 'Prerequisite: ECE 511 and 526.', 'Fall, Spring', 0, 'None', NULL, NULL),
(1418, 1, 1, 'ECE', 511, 'ECE511', 'Circuit Theory II', 'Analysis of electric circuits using differential equations, transform techniques, and linear algebra. Transmission lines and applications.', 3, 3, 0, 'Prerequisite: MATH 240, STAT 510, and ECE 410.', 'Fall, Spring', 0, 'None', NULL, NULL),
(1419, 1, 1, 'ECE', 512, 'ECE512', 'Linear Systems', 'Time- and frequency-domain analysis of signals and systems. Concepts include signal classification, convolution, Fourier series, Fourier transforms, filters, signal sampling, and discrete transforms.', 3, 3, 0, 'Prerequisite: ECE 511 and 540.', 'Fall, Spring', 0, 'None', NULL, NULL),
(1420, 1, 1, 'ECE', 519, 'ECE519', 'Electric Circuits and Control', 'Principles of direct-current circuits and machines, alternating-current circuits and machines, electronics, and application to instrumentation and control.', 4, 4, 0, 'Prerequisite: PHYS 214.', 'Fall, Spring, Summer', 0, 'None', NULL, NULL),
(1421, 1, 1, 'ECE', 525, 'ECE525', 'Electronics I', 'Fundamentals of electronic components (e.g., diodes, MOSFETs, BJTs, op amps) and electronic circuits.', 3, 3, 0, 'Prerequisite: ECE 410 or 519.', 'Fall, Spring', 0, 'None', NULL, NULL),
(1422, 1, 1, 'ECE', 526, 'ECE526', 'Electronics II', 'Continuation of Electronics I, with emphasis on performance measures, including frequency response. Additional topics include Miller’s theorem, cascoding, and the application of negative feedback.', 3, 3, 0, 'Prerequisite: ECE 511 and 525.', 'Fall, Spring', 0, 'None', NULL, NULL),
(1423, 1, 1, 'ECE', 530, 'ECE530', 'Control Systems Design', 'Modeling, analysis, and design of control systems. Topics include feedback control; time response and stability of dynamic systems; introduction to root locus and frequency response design.', 3, 3, 0, 'Prerequisite: MATH 240 and ECE 512.', 'Fall, Spring', 0, 'None', NULL, NULL),
(1424, 1, 1, 'ECE', 540, 'ECE540', 'Applied Scientific Computing for Engineers', 'Review of the C programming language with emphasis on solving various classes of numerical problems.&nbsp; General topics, such as the precision of floating point numbers are also discussed.&nbsp; Statistics, linear algebra and optimization are the primary classes of numerical problems addressed.', 3, 3, 0, 'Prerequisite: STAT 510 and CIS 209 or 308.', 'Fall, Spring', 0, 'None', NULL, NULL),
(1425, 1, 1, 'ECE', 542, 'ECE542', 'Local Area Networking', 'An introduction to data communication concepts used in the network, data link, and physical layers of the Open Systems Interconnection (OSI) model. Hardware and software aspects of data communications as well as modern Local Area Network (LAN) standards will be emphasized.', 3, 3, 0, 'Prerequisite: ECE 241, high-level programming language.', 'Fall, Spring', 0, 'None', NULL, NULL),
(1426, 1, 1, 'ECE', 557, 'ECE557', 'Electromagnetic Theory I', 'Vector analysis, electrostatics, magnetostatics, Faraday’s Law, Maxwell’s Equations, plane waves, and applications.', 3, 3, 0, 'Prerequisite: PHYS 214 and ECE 410.', 'Fall, Spring', 0, 'None', NULL, NULL),
(1427, 1, 1, 'ECE', 571, 'ECE571', 'Introduction to Biomedical Engineering', 'Overview of engineering applied to clinical medicine and the life sciences. Topics include sensors and instrumentation to acquire physiologic data, imaging techniques, biomechanics, health care information technology, assistive technology, telemedicine, home care, emergency services, battlefield medicine, and biomedical career opportunities.', 3, 3, 0, 'Prerequisite: MATH 222 and PHYS 214.', 'Spring', 0, 'None', NULL, NULL),
(1428, 1, 1, 'ECE', 581, 'ECE581', 'Energy Conversion I', 'Three-phase systems, magnetic circuits, transformers, and DC and AC machines.', 3, 3, 0, 'Prerequisite: ECE 410 or 519.', 'Fall, Spring', 0, 'None', NULL, NULL),
(1429, 1, 1, 'ECE', 582, 'ECE582', 'Wind Energy Research', 'This course provides an opportunity to pursue one or more longer-term research questions related to wind engineering, including small-wind siting and installation and meteorological-tower installation and data analysis.', 1, 1, 0, 'Prerequisite: Instructor permission.', 'Spring', 0, 'None', NULL, NULL),
(1430, 1, 1, 'ECE', 590, 'ECE590', 'Seminar', 'Review of oral and written communication skills, engineering ethics and career topics.', 1, 1, 0, 'Prerequisite: DEN 325 and ENGL 415.', 'Fall, Spring', 0, 'Ethical Reasoning and Responsibility', NULL, NULL),
(1431, 1, 1, 'ECE', 624, 'ECE624', 'Power Electronics', 'Theory and application of semiconductor devices to the control and conversion of electric power, control of DC and AC machines, and design of electronic power circuits such as controlled rectifiers, converters and inverters.', 3, 3, 0, 'Prerequisite: ECE 581, 511, and 525.', 'Fall', 0, 'None', NULL, NULL),
(1432, 1, 1, 'ECE', 628, 'ECE628', 'Electronic Instrumentation', 'Applications of electronics in the design of analog and digital systems for the measurement of physical variables and in the transduction of these variables into a useful form for both recording and control.', 3, 3, 0, 'Prerequisite: ECE 502 and 526.', 'Fall, Spring', 0, 'None', NULL, NULL),
(1433, 1, 1, 'ECE', 631, 'ECE631', 'Microcomputer Systems Design', 'Design and engineering application of 32-bit microcomputers to instrumentation and control. Investigate the relationship of the C language and assembly language. Timing and other interfacing problems will be covered.', 3, 3, 0, 'Prerequisite: CIS 308 or 209 or ME 400; ECE 431; ECE 525 or 519.', 'Spring', 0, 'None', NULL, NULL),
(1434, 1, 1, 'ECE', 633, 'ECE633', 'Real-Time Embedded Systems', 'Interconnection of peripherals, such as CAN networks, DA/AD converters, and Timers. Implementation of device drivers on top of micro-kernels. Build a simple real time distributed embedded system.', 1, 1, 0, 'Prerequisite: CIS 621 and CIS 622.', 'Fall', 0, 'None', NULL, NULL),
(1435, 1, 1, 'ECE', 636, 'ECE636', 'Introduction to Computer Graphics', 'An introduction to the software, hardware, and algorithmic aspects of graphics generation. Programming assignments will provide experience in implementing interactive interfaces via application-level graphics libraries.', 3, 3, 0, 'Prerequisite: CIS 308 or 209; CIS 300; and MATH 222 or 551.', 'Fall, Spring', 0, 'None', NULL, NULL),
(1436, 1, 1, 'ECE', 641, 'ECE641', 'Advanced Digital Design using Logic Synthesis', 'Applications of hardware description languages (HDLs) for the design of complex digital systems. Topics include designing and simulating using HDLs, logic synthesis into FPGAs and ASICs, optimization techniques, timing issues, hardware verification, and design for testability.', 3, 3, 0, 'Prerequisite: ECE 441.', 'Spring', 0, 'None', NULL, NULL),
(1437, 1, 1, 'ECE', 643, 'ECE643', 'Computer Engineering Design Lab', 'The design and construction of a small computer system using simple programmable devices. The design and construction of computer interfacing systems for PCs based on USB devices and simple microcontroller chips.', 3, 3, 0, 'Prerequisite: CIS 308 or 209; and ECE 441. Prerequisite or concurrent enrollment: ECE 649.', 'Fall, Spring', 0, 'None', NULL, NULL),
(1438, 1, 1, 'ECE', 645, 'ECE645', 'Digital Electronics', 'Design of high-performance digital electronics and systems. Topics include design metrics, device modeling, transmission-line models for wiring, signaling conventions, transmitter and receiver circuits, noise analysis and management, power distribution, timing and synchronization techniques.', 3, 3, 0, 'Prerequisite: ECE 511, 525, and 441.', 'Spring', 0, 'None', NULL, NULL),
(1439, 1, 1, 'ECE', 647, 'ECE647', 'Digital Filtering', 'Difference equation characterization of digital filters, transient and steady-state analysis of digital filters using the Z-transform, spectral analysis of digital signals, design and implementation of digital filters.', 3, 3, 0, 'Prerequisite: ECE 512.', 'Fall', 0, 'None', NULL, NULL),
(1440, 1, 1, 'ECE', 648, 'ECE648', 'Multimedia Compression', 'Introduction to multimedia creation and representation. Design of multimedia systems, which incorporate audio, image and video. Topics will include the analysis and design of multimedia compression, streaming, delivery, security and authoring. Emphasis will be placed on current multimedia standards and applications.', 3, 3, 0, 'Prerequisite: ECE 512 or MATH 551; CIS 308 or 209.', 'Spring', 0, 'None', NULL, NULL),
(1441, 1, 1, 'ECE', 649, 'ECE649', 'Computer Design I', 'Concepts of computer design.&nbsp; Information representation, instruction sets and addressing modes.&nbsp; Arithmetic and logic unit design for fixed and floating point operations. Hardwired and microprogrammed control design. Concepts of pipelining, CISC and RISC architecture. Memory system design including virtual memory, caches and interleaved memories. I/O design methods, interrupt mechanisms, DMA and system integration.', 3, 3, 0, 'Prerequisite: ECE 441.', 'Fall, Spring', 0, 'None', NULL, NULL),
(1442, 1, 1, 'ECE', 660, 'ECE660', 'Communication Systems I', 'Introduction to the analysis and design of analog and digital communication systems. Topics include analog and digital modulation schemes, digital encoding of messages, mathematical modeling of communications systems, noise in communication links and calculation of performance measures for practical links.', 3, 3, 0, 'Prerequisite or concurrent enrollment: ECE 512.', 'Fall', 0, 'None', NULL, NULL),
(1443, 1, 1, 'ECE', 661, 'ECE661', 'Communication Systems II', 'Error-correction code design and analysis with emphasis on block and convolutional coding and decoding; Trellis-coded modulation. Modeling of bandlimited systems and pulse-shape design, optimal receiver design for bandlimited systems, linear and nonlinear equalization. Multiple-access techniques, orthogonal frequency-division multiplexing and receiver design for CDMA schemes. Simulation of practical digital transmission systems.', 3, 3, 0, 'Prerequisite: ECE 660.', 'Spring', 0, 'None', NULL, NULL),
(1444, 1, 1, 'ECE', 662, 'ECE662', 'Design of Communication Circuits', 'The design of communication circuits and systems operating from baseband to UHF frequencies. Topics include tuned-RF amplifiers, oscillators, mixers, LC and ceramic bandpass filters, and demodulators. Projects involve the design, construction, and performance testing of a complete radio receiver.', 3, 3, 0, 'Prerequisite: ECE 526 and 502.', 'Spring', 0, 'None', NULL, NULL),
(1445, 1, 1, 'ECE', 670, 'ECE670', 'Engineering Applications of Machine Intelligence', 'Study of machine intelligence and fuzzy logic concepts and applications in engineering problem domains. As a term project, develop a fuzzy expert system for a specific problem domain that runs on a personal computer and develop the supporting documentation.', 3, 3, 0, 'Prerequisite: CIS 200 or 209, and PHYS 214.', 'Spring', 0, 'None', NULL, NULL),
(1446, 1, 1, 'ECE', 681, 'ECE681', 'Wind and Solar Engineering', 'Wind and solar energy resource assessment, operation of renewable-energy systems, photovoltaic and wind-turbine physics, and design of complete systems.', 3, 3, 0, 'Prerequisite: ECE 525 or 519.', 'Fall', 0, 'None', NULL, NULL),
(1447, 1, 1, 'ECE', 684, 'ECE684', 'Power Laboratory', 'Introduction to power systems and device analysis. Course includes lecture and laboratory experience in aspects of power flow, system operation, power quality, power electronics, and economic analysis.', 3, 3, 0, 'Prerequisite: ECE 525 and 581.', 'Spring', 0, 'None', NULL, NULL),
(1448, 1, 1, 'ECE', 685, 'ECE685', 'Power Systems Design', 'A comprehensive study of modeling of the electric power system components and computer simulation of interconnected power systems in steady state. Vector-matrix descriptions are emphasized.', 3, 3, 0, 'Prerequisite: ECE 581.', 'Fall', 0, 'None', NULL, NULL),
(1449, 1, 1, 'ECE', 686, 'ECE686', 'Power Systems Protection', 'Analysis of symmetrical and unsymmetrical faults on power systems using symmetrical components technique. Study of protective relaying for protection of power systems against faults. Vector-matrix descriptions and computer solutions are emphasized.', 3, 3, 0, 'Prerequisite: ECE 581.', 'Spring', 0, 'None', NULL, NULL),
(1450, 1, 1, 'ECE', 690, 'ECE690', 'Problems in Electrical and Computer Engineering', 'Problems in Electrical and Computer Engineering', 0, 18, 1, '', 'Fall, Spring, Summer', 0, 'None', NULL, NULL),
(1451, 1, 1, 'ECE', 694, 'ECE694', 'Optoelectronics', 'Applied geometric and physical optics, optical radiation, and the interaction of light and matter. The theory and application of photodetectors, lasers, and other photoemitters. Introduction to fiber optical waveguides, sensors, and systems.', 3, 3, 0, 'Prerequisite: ECE 525, 557, and CHE 350.', 'Fall', 0, 'None', NULL, NULL),
(1452, 1, 1, 'ECE', 696, 'ECE696', 'Integrated Circuit Design', 'Study of silicon integrated circuits with emphasis on CMOS analog and digital applications. The course covers basic device structure and modeling, circuit analysis, system design, IC design methodology and economics plus IC fabrication processes. Computer-aided tools are used to simulate and layout circuits designed by student groups. The circuits are fabricated by an external service (MOSIS).', 3, 3, 0, 'Prerequisite: ECE 241 and 525.', 'Fall', 0, 'None', NULL, NULL),
(1453, 1, 1, 'ECE', 715, 'ECE715', 'Electroacoustics', 'Basic principles of sound; modeling of, and analogous circuits for, mechanical and acoustical systems; microphones and loudspeakers; Thiele-Small parameters; the analysis and design of, and measurements on, common loudspeaker systems and crossover networks. Projects involve the design, simulation, construction and performance-testing of a complete loudspeaker-and-enclosure system.', 3, 3, 0, 'Prerequisite: ECE 511.', 'Fall', 0, 'None', NULL, NULL),
(1454, 1, 1, 'ECE', 722, 'ECE722', 'Audio Engineering', 'Solid-state and hollow-state analog electronics applicable to high-fidelity audio. Emphasis is on the analysis, design and construction of audio amplifiers. Projects include design, implementation, and performance-evaluation of a reference-quality audio power amplifier.', 3, 3, 0, 'Prerequisite: ECE 525. Recommended prerequisite: ECE 526.', 'Spring', 0, 'None', NULL, NULL),
(1455, 1, 1, 'ECE', 724, 'ECE724', 'Analog Electronics', 'Analysis, design, and evaluation of BJT and CMOS operational amplifiers. Core topics include frequency compensation and closed-loop stability, slew-rate optimization, wide-bandwidth design, low-noise design, and macromodeling for simulation.', 3, 3, 0, 'Prerequisite: ECE 502, ECE 526.', 'Spring', 0, 'None', NULL, NULL),
(1456, 1, 1, 'ECE', 725, 'ECE725', 'Integrated Circuit Devices and Processes', 'Integrated circuit fabrication processes including oxidation, diffusion, ion implantation, lithography, etching, crystal growth, process monitoring, and device characterization. Design of discrete and IC devices through laboratory experiments and computer simulations.', 3, 3, 0, 'Prerequisite: ECE 525 and CHE 350.', 'Spring', 0, 'None', NULL, NULL),
(1457, 1, 1, 'ECE', 728, 'ECE728', 'Mixed Signal Measurements', 'Signal classification, noise and uncertainty, TRMS conversion, quantization and ADCs, repetitive sampling and signal recovery techniques, vector voltmeters, basic network analyzers.', 3, 3, 0, 'Prerequisite: ECE 512 or graduate standing.', 'On sufficient demand', 0, 'None', NULL, NULL),
(1458, 1, 1, 'ECE', 730, 'ECE730', 'Control Systems Analysis and Design', 'Use of classical analysis techniques for control system compensation. State space control theory fundamentals are presented in addition to an introductory treatment of several major systems areas.', 3, 3, 0, 'Prerequisite: ECE 530 or ME 640; ME 730.', 'Spring', 0, 'None', NULL, NULL),
(1459, 1, 1, 'ECE', 731, 'ECE731', 'Advanced Microcomputer System Design', 'Design and engineering applications of 16 and 32 bit microprocessors. Utilization of peripheral and co-processor chips.', 3, 3, 0, 'Prerequisite: ECE 631.', 'Spring', 0, 'None', NULL, NULL),
(1460, 1, 1, 'ECE', 733, 'ECE733', 'Real-Time Embedded Systems Design', 'Design and implementation of a comprehensive team project of a complete embedded real-time system.', 3, 3, 0, 'Prerequisite or concurrent enrollment: CIS 721.', 'Spring', 0, 'None', NULL, NULL),
(1461, 1, 1, 'ECE', 736, 'ECE736', 'Discrete-Time and Computer-Control Systems', 'Analysis and design of discrete-time, sampled-data, and computer-control systems using discrete-state equations and Z-transforms.', 3, 3, 0, 'Prerequisite: ECE 530 or ME 640.', 'Spring', 0, 'None', NULL, NULL),
(1462, 1, 1, 'ECE', 746, 'ECE746', 'Fault Diagnosis in Digital Systems', 'Hazards, fault detection in combinational circuits, and sequential machines using path sensitizing and fault-matrix methods, state table analysis, etc.; system reliability through logical redundance.', 3, 3, 0, 'Prerequisite or concurrent enrollment: ECE 541 or 631.', 'Spring, even years', 0, 'None', NULL, NULL),
(1463, 1, 1, 'ECE', 747, 'ECE747', 'Digital Signal Processing Laboratory', 'Digitization of analog signals; demonstration of aliasing problems; spectral analysis of digital signals using Fourier and other signal representation techniques; digital filtering problems; applications related to biomedical and speech data.', 3, 3, 0, 'Prerequisite: ECE 512. Prerequisite or concurrent enrollment: ECE 647.', 'Spring', 0, 'None', NULL, NULL),
(1464, 1, 1, 'ECE', 749, 'ECE749', 'Computer Design II', 'Study of alternate computer hardware structures. Investigation of engineering tradeoffs in implementation of alternative instruction sets and computing structures. Emphasis will be placed on a quantitative approach to cost/performance evaluations including simulation of hardware structures.', 3, 3, 0, 'Prerequisite: ECE 649.', 'Fall', 0, 'None', NULL, NULL),
(1465, 1, 1, 'ECE', 758, 'ECE758', 'Electromagnetic Theory II', 'Continuation of ECE 557.', 3, 3, 0, 'Prerequisite: ECE 557.', 'On sufficient demand', 0, 'None', NULL, NULL),
(1466, 1, 1, 'ECE', 760, 'ECE760', 'Wireless Communications', 'This course exposes seniors and first year graduate students to important physical layer concepts in wireless communications. Topics include: cellular architecture, large scale and small scale fading channel models, diversity receivers, DS-CDMA transmitter and receiver design, multi-user detection, multi-carrier CDMA and OFDM performance analysis.', 3, 3, 0, 'Prerequisite: ECE 660.', 'Spring', 0, 'None', NULL, NULL),
(1467, 1, 1, 'ECE', 764, 'ECE764', 'Design of Microwave Circuits', 'The design of communication circuits and systems operating at microwave frequencies. Topics include antennas, transmission lines, microstrip matching networks, S-parameters, frequency synthesizers, and downconverter components such as LNAs, mixers, and microstrip bandpass filters. Projects involve design, simulation with electronic design automation tools, and laboratory measurements.', 3, 3, 0, 'Prerequisite: ECE 502, 512, 526, and 557.', 'Fall', 0, 'None', NULL, NULL),
(1468, 1, 1, 'ECE', 765, 'ECE765', 'Digital Radio Hardware Design', 'Advanced topics in digital radio communication systems. Topics include the design and application of state-of-the-art RF and baseband circuits found in products ranging from cordless and cellular phones to wireless local area networks. System-level issues including coding, duplexing, and multiple access techniques are also covered, and a team-based project provides experience with RF hardware research and development activities.', 3, 3, 0, 'Prerequisite or concurrent enrollment: ECE 696; Prerequisite: ECE 622 or ECE 764 or consent of instructor.', 'On sufficient demand', 0, 'None', NULL, NULL),
(1469, 1, 1, 'ECE', 771, 'ECE771', 'Control Theory Applied to Bioengineering', 'Development of mathematical models used in the study and analysis of physiological control systems providing techniques for varying pertinent biological parameters.', 3, 3, 0, 'Prerequisite or concurrent: ECE 530 or ME 640, and a basic physiology course.', 'Spring', 0, 'None', NULL, NULL),
(1470, 1, 1, 'ECE', 772, 'ECE772', 'Theory and Techniques of Bioinstrumentation', 'Theoretical aspects of biological signals, electrodes, transducers, digital imaging and computer-based data acquisition directed toward ECE and other science department majors.', 2, 2, 0, 'Prerequisite: Concurrent enrollment in ECE 773 (ECE majors only) and AP 773.', 'Fall', 0, 'None', NULL, NULL),
(1471, 1, 1, 'ECE', 773, 'ECE773', 'Bioinstrumentation Design Laboratory', 'Design and testing of hardware and software for acquiring and analyzing biological signals.', 1, 1, 0, 'Prerequisite: ECE 502 and concurrent enrollment in ECE 772 and AP 773.', 'Fall', 0, 'None', NULL, NULL),
(1472, 1, 1, 'ECE', 780, 'ECE780', 'Power Seminar', 'Speakers from industry, academia, and government present topics related to power systems engineering.', 1, 1, 0, 'Prerequisite: Junior standing.', 'Fall', 0, 'None', NULL, NULL);